    public static ArrayList<HashMap<String,Object>> ERROR_LIST = new ArrayList<>();

    /**
     * @param savePath 需要以"/"结尾！
     * */
    public static void downloadModpacks(String zipPath, String savePath){
        ERROR_LIST = new ArrayList<>();
        String manifest = ZipHandler.readFromZip(zipPath, "manifest.json");
        HashMap<String,Object> map = JsonReader.readFJ(manifest);
        String name = (String) map.get(Concept.NAME);
        ArrayList<HashMap<String,Object>> mods = (ArrayList<HashMap<String, Object>>) map.get(Concept.FILES);
        for (HashMap<String,Object> map2 : mods){
            String downloadPath;
            if (Boolean.parseBoolean((String) map2.get(Concept.REQUIRED))) downloadPath = savePath;
//                downloadPath = savePath + "necessary/";
            else downloadPath = savePath + "optional/";
            Mixture<Boolean,String> mixture = downloadFromGet(Integer.parseInt((String) map2.get(Concept.PROJECT_ID)),
                    Integer.parseInt((String) map2.get(Concept.FILE_ID)),downloadPath);
            if (!mixture.getKey()){
                map2.put(Concept.FILE_NAME,mixture.getValue());
                ERROR_LIST.add(map2);
            }

        }
    }

    private static Mixture<Integer,String> get(int modId, int fileId) {
        Mixture<Integer,String> result = new Mixture<>(0,null);
        HttpClient client = HttpClient.newHttpClient();
        HttpRequest request = HttpRequest.newBuilder()
                .uri(URI.create("https://api.curseforge.com/v1/mods/"+modId+"/files/"+fileId+"/download-url")) // 替换为你的目标URL
                .header("x-api-key", ConfigHandler.CONFIG.getCf_api_key()) // 设置请求头
                .GET() // 设置请求体
                .build();
        try {
            HttpResponse<String> response = client.send(request, HttpResponse.BodyHandlers.ofString());
            result.setKey(response.statusCode());
            result.setValue(response.body());
        } catch (Exception e) {
            e.printStackTrace();
        }
        return result;
    }

    /**
     * @param path 路径需要以"/"结尾！
     *      downloadFromGet方法比downloadFromMoreInfo方法更快，所以并不使用downloadFromMoreInfo方法作为主要下载方式，而是将其作为备选下载方案。
     * */
    private static Mixture<Boolean,String> downloadFromGet(int modId, int fileId, String path) {
        Mixture<Integer, String> mixture = get(modId, fileId);
        if (mixture.getKey() == 200) {
            String json = mixture.getValue();
            String url = json.substring(json.indexOf(":\"") + ":\"".length(), json.lastIndexOf("\""));
            mainFrame.logger.info("---获得地址" + url);
            String name = url.substring(url.lastIndexOf("/") + 1);
            mainFrame.logger.info("正在下载" + name + "模组文件......");
            FileHandler.downloadFile(url, path, name, ConfigHandler.CONFIG.isCf_doa());
            return new Mixture<>(true,name);
        } else {
            mainFrame.logger.warn("Response Code: " + mixture.getKey());
            mainFrame.logger.error("下载模组(modId/projectID): "+modId+"，文件码(fileId): "+fileId+"失败！");
            mainFrame.logger.error("尝试备选下载方案......");
            return downloadFromMoreInfo(modId, fileId, path);
        }
    }

    private static Mixture<Integer,String> moreInfo(int modId){
        Mixture<Integer,String> result = new Mixture<>(0,null);
        HttpClient client = HttpClient.newHttpClient();
        HttpRequest request = HttpRequest.newBuilder()
                .uri(URI.create("https://api.curseforge.com/v1/mods/"+modId+"/files/")) // 替换为你的目标URL
                .header("x-api-key", ConfigHandler.CONFIG.getCf_api_key()) // 设置请求头
                .GET() // 设置请求体
                .build();
        try {
            HttpResponse<String> response = client.send(request, HttpResponse.BodyHandlers.ofString());
            result.setKey(response.statusCode());
            result.setValue(response.body());
        } catch (Exception e) {
            e.printStackTrace();
        }
        return result;
    }

    private static String getName(String json, int fileId){
        json = json.substring(json.indexOf(String.valueOf(fileId))+String.valueOf(fileId).length());
        json = json.substring(json.indexOf("\"fileName\":\"")+"\"fileName\":\"".length());
        return json.substring(0,json.indexOf("\""));
    }

    private static Mixture<Boolean,String> downloadFromMoreInfo(int modId, int fileId, String path){
        Mixture<Integer, String> mixture = moreInfo(modId);
        if (mixture.getKey()==200){
            String name = getName(mixture.getValue(),fileId);
            String _id = String.valueOf(fileId);
            String url = "https://mediafilez.forgecdn.net/files/"+_id.substring(0,4)+"/"+_id.substring(4)+"/"+name;
            mainFrame.logger.info("---获得地址" + url);
            mainFrame.logger.info("正在下载" + name + "模组文件......");
            FileHandler.downloadFile(url, path, name, ConfigHandler.CONFIG.isCf_doa());
            return new Mixture<>(true,name);
        } else {
            mainFrame.logger.warn("备选下载方案Response Code: "+mixture.getKey());
            mainFrame.logger.error("备选下载方案无法获取该模组的更多信息！");
            mainFrame.logger.warn("Ignoring...");
            return new Mixture<>(false,null);
        }
    }
